mapper.select:
SELECT r.user_id,r.type,r.balk_no,r.balk_content,r.write_dept_name,r.intro, ifnull(r.content_key,'其他') AS content_key, ifnull(r.proc_key,'其他') AS proc_key, ifnull(r.problem,'其他') AS problem, ifnull(r.reason,'其他') AS reason FROM result AS r WHERE r.problem IN
#{item}
ORDER BY r.problem

